// src/components/Balance.js
import React from 'react';

const Balance = ({ walletAddress, balance }) => {
    return (
        <div className="mt-4">
            <h5>Balance</h5>
            {walletAddress ? (
                <>
                    <p>{balance} ETH</p>
                    {/* Optionally display balance in USD if you fetch it */}
                </>
            ) : (
                <p>Please connect your wallet.</p>
            )}
        </div>
    );
};

export default Balance;
